function bindTabs(t){"string"==typeof t&&(t=document.querySelector(t));const e=t.querySelectorAll(".main-tab-item"),n=t.querySelectorAll(".tab-content");for(let t=0;t<e.length;t++){e[t].addEventListener("click",()=>{c(e),c(n),e[t].classList.add("active"),n[t].classList.add("active")})}function c(t){for(let e=0;e<t.length;e++){t[e].classList.remove("active")}}}bindTabs(".main");